mapper.select:
/* SQL ID: www.api.brd.boardDef.BoardDef.selectBoardDefList Description : 목록 Query */ SELECT BOARD_ID ,BOARD_CD ,BOARD_NM ,SLUG ,READ_LVL_CD ,WRITE_LVL_CD ,USE_COMMENT_AT ,USE_ATTACH_AT ,SORT_DEFAULT_CD ,USE_AT ,DATE_FORMAT(CREATED_DT, '%Y-%m-%d %H:%i:%s') AS CREATED_DT ,CREATED_BY ,DATE_FORMAT(UPDATED_DT, '%Y-%m-%d %H:%i:%s') AS UPDATED_DT ,UPDATED_BY
FROM TB_BOARD_DEF
ORDER BY BOARD_ID DESC
/* 페이징 하단 시작 */ LIMIT #{limit} OFFSET #{offset} /* 페이징 하단 끝 */

mapper.insert:
        /*
         SQL ID: www.api.brd.boardDef.BoardDef.insertBoardDef
         Description : 등록 Query 
        */
        INSERT INTO TB_BOARD_DEF
        (
            BOARD_CD
			,BOARD_NM
			,SLUG
			,READ_LVL_CD
			,WRITE_LVL_CD
			,USE_COMMENT_AT
			,USE_ATTACH_AT
			,SORT_DEFAULT_CD
			,USE_AT
			,CREATED_DT
			,CREATED_BY
			,UPDATED_DT
			,UPDATED_BY

        )
        VALUES
        (
            #{boardCd}
			,#{boardNm}
			,#{slug}
			,#{readLvlCd}
			,#{writeLvlCd}
			,#{useCommentAt}
			,#{useAttachAt}
			,#{sortDefaultCd}
			,#{useAt}
			,IFNULL(STR_TO_DATE(#{createdDt}, '%Y-%m-%d %H:%i:%s'), NOW())
			,#{createdBy}
			,IFNULL(STR_TO_DATE(#{updatedDt}, '%Y-%m-%d %H:%i:%s'), NOW())
			,#{updatedBy}

        )

mapper.delete:
/*
 SQL ID: www.api.brd.boardDef.BoardDef.deleteBoardDef
 Description : 삭제 Query 
*/
DELETE FROM TB_BOARD_DEF
WHERE BOARD_ID = #{boardId}
