mapper.select:
/* SQL ID: www.api.crt.cart.Cart.selectCartList Description : 목록 Query */ SELECT CART_ID ,USER_ID ,STATUS_CD ,USE_AT ,DATE_FORMAT(CREATED_DT, '%Y-%m-%d %H:%i:%s') AS CREATED_DT ,CREATED_BY ,DATE_FORMAT(UPDATED_DT, '%Y-%m-%d %H:%i:%s') AS UPDATED_DT ,UPDATED_BY
FROM TB_CART
ORDER BY CART_ID DESC
/* 페이징 하단 시작 */ LIMIT #{limit} OFFSET #{offset} /* 페이징 하단 끝 */

mapper.insert:
/*
 SQL ID: www.api.crt.cart.Cart.insertCart
 Description : 등록 Query (Javagen CRUD용)
*/
INSERT INTO TB_CART
(
    USER_ID
    ,STATUS_CD
    ,USE_AT
    ,CREATED_DT
    ,CREATED_BY
    ,UPDATED_DT
    ,UPDATED_BY

)
VALUES
(
    #{userId}
    ,#{statusCd}
    ,#{useAt}
    ,IFNULL(STR_TO_DATE(NULLIF(CONCAT(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{createdDt}, 'T',' '), '.', 1),'Z',''),'T',' '), CASE WHEN LENGTH(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{createdDt}, 'T',' '), '.', 1),'Z',''),'T',' '))=16 THEN ':00' ELSE '' END),''), '%Y-%m-%d %H:%i:%s'), NOW())
    ,#{createdBy}
    ,IFNULL(STR_TO_DATE(NULLIF(CONCAT(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{updatedDt}, 'T',' '), '.', 1),'Z',''),'T',' '), CASE WHEN LENGTH(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{updatedDt}, 'T',' '), '.', 1),'Z',''),'T',' '))=16 THEN ':00' ELSE '' END),''), '%Y-%m-%d %H:%i:%s'), NOW())
    ,#{updatedBy}

)

mapper.delete:
/*
 SQL ID: www.api.crt.cart.Cart.deleteCart
 Description : 삭제 Query (물리삭제, 관리자용)
*/
DELETE FROM TB_CART
WHERE CART_ID = #{cartId}
