mapper.select:
/* SQL ID: www.api.sys.cmmnCodeCl.CmmnCodeCl.selectCmmnCodeClList Description : 목록 Query */ SELECT CODE_CL_ID ,CODE_CL ,CODE_CL_NM ,USE_AT ,DATE_FORMAT(CREATED_DT, '%Y-%m-%d %H:%i:%s') AS CREATED_DT ,DATE_FORMAT(UPDATED_DT, '%Y-%m-%d %H:%i:%s') AS UPDATED_DT
FROM TB_CMMN_CODE_CL
ORDER BY CODE_CL_ID DESC
/* 페이징 하단 시작 */ LIMIT #{limit} OFFSET #{offset} /* 페이징 하단 끝 */

mapper.insert:
        /*
         SQL ID: www.api.sys.cmmnCodeCl.CmmnCodeCl.insertCmmnCodeCl
         Description : 등록 Query 
        */
        INSERT INTO TB_CMMN_CODE_CL
        (
            CODE_CL
			,CODE_CL_NM
			,USE_AT
			,CREATED_DT
			,UPDATED_DT

        )
        VALUES
        (
            #{codeCl}
			,#{codeClNm}
			,#{useAt}
			,IFNULL(STR_TO_DATE(NULLIF(CONCAT(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{createdDt}, 'T',' '), '.', 1),'Z',''),'T',' '), CASE WHEN LENGTH(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{createdDt}, 'T',' '), '.', 1),'Z',''),'T',' '))=16 THEN ':00' ELSE '' END),''), '%Y-%m-%d %H:%i:%s'), NOW())
			,IFNULL(STR_TO_DATE(NULLIF(CONCAT(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{updatedDt}, 'T',' '), '.', 1),'Z',''),'T',' '), CASE WHEN LENGTH(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{updatedDt}, 'T',' '), '.', 1),'Z',''),'T',' '))=16 THEN ':00' ELSE '' END),''), '%Y-%m-%d %H:%i:%s'), NOW())

        )

mapper.delete:
/*
 SQL ID: www.api.sys.cmmnCodeCl.CmmnCodeCl.deleteCmmnCodeCl
 Description : 삭제 Query 
*/
DELETE FROM TB_CMMN_CODE_CL
WHERE CODE_CL_ID = #{codeClId}
