mapper.select:
/* SQL ID: www.api.sys.cmmnCode.CmmnCode.selectCmmnCodeList Description : 목록 Query */ SELECT CODE_ID ,CODE_CL_ID ,CODE ,CODE_NM ,SORT_ORDR ,USE_AT ,DATE_FORMAT(CREATED_DT, '%Y-%m-%d %H:%i:%s') AS CREATED_DT ,DATE_FORMAT(UPDATED_DT, '%Y-%m-%d %H:%i:%s') AS UPDATED_DT
FROM TB_CMMN_CODE
ORDER BY CODE_ID DESC
/* 페이징 하단 시작 */ LIMIT #{limit} OFFSET #{offset} /* 페이징 하단 끝 */

mapper.insert:
        /*
         SQL ID: www.api.sys.cmmnCode.CmmnCode.insertCmmnCode
         Description : 등록 Query 
        */
        INSERT INTO TB_CMMN_CODE
        (
            CODE_CL_ID
			,CODE
			,CODE_NM
			,SORT_ORDR
			,USE_AT
			,CREATED_DT
			,UPDATED_DT

        )
        VALUES
        (
            #{codeClId}
			,#{code}
			,#{codeNm}
			,#{sortOrdr}
			,#{useAt}
			,IFNULL(STR_TO_DATE(NULLIF(CONCAT(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{createdDt}, 'T',' '), '.', 1),'Z',''),'T',' '), CASE WHEN LENGTH(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{createdDt}, 'T',' '), '.', 1),'Z',''),'T',' '))=16 THEN ':00' ELSE '' END),''), '%Y-%m-%d %H:%i:%s'), NOW())
			,IFNULL(STR_TO_DATE(NULLIF(CONCAT(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{updatedDt}, 'T',' '), '.', 1),'Z',''),'T',' '), CASE WHEN LENGTH(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{updatedDt}, 'T',' '), '.', 1),'Z',''),'T',' '))=16 THEN ':00' ELSE '' END),''), '%Y-%m-%d %H:%i:%s'), NOW())

        )

mapper.delete:
/*
 SQL ID: www.api.sys.cmmnCode.CmmnCode.deleteCmmnCode
 Description : 삭제 Query 
*/
DELETE FROM TB_CMMN_CODE
WHERE CODE_ID = #{codeId}
