mapper.select:
SELECT DIARY_ID, OWNER_ID, GRP_CD, DATE_FORMAT(DIARY_DT, '%Y-%m-%d') AS DIARY_DT, CONTENT, FILE_GRP_ID, USE_AT, DATE_FORMAT(CREATED_DT, '%Y-%m-%d %H:%i:%s') AS CREATED_DT, DATE_FORMAT(UPDATED_DT, '%Y-%m-%d %H:%i:%s') AS UPDATED_DT FROM TB_DIARY
ORDER BY DIARY_DT DESC, DIARY_ID DESC
LIMIT #{limit} OFFSET #{offset}

SELECT DIARY_ID, OWNER_ID, GRP_CD, DATE_FORMAT(DIARY_DT, '%Y-%m-%d') AS DIARY_DT, CONTENT, FILE_GRP_ID, USE_AT, DATE_FORMAT(CREATED_DT, '%Y-%m-%d %H:%i:%s') AS CREATED_DT, DATE_FORMAT(UPDATED_DT, '%Y-%m-%d %H:%i:%s') AS UPDATED_DT FROM TB_DIARY WHERE USE_AT = 'Y'
AND GRP_CD = #{grpCd}
AND DATE(DIARY_DT) = #{diaryDt} ORDER BY DIARY_ID DESC LIMIT 1
