mapper.select:
/* SQL ID: www.api.isu.issue.Issue.selectIssueList Description : 목록 Query */ SELECT ISSUE_ID ,GRP_CD ,PRJ_ID ,ISSUE_NO ,ISSUE_KEY ,ISSUE_TYPE_CD ,SUMMARY ,DESCRIPTION ,PRIORITY_CD ,STATUS_CD ,REPORTER_ID ,ASSIGNEE_ID ,PARENT_ISSUE_ID ,DATE_FORMAT(START_DT, '%Y-%m-%d %H:%i:%s') AS START_DT ,DATE_FORMAT(DUE_DT, '%Y-%m-%d %H:%i:%s') AS DUE_DT ,ESTIMATE_MIN ,SPENT_MIN ,ORDER_IN_PRJ ,DATE_FORMAT(CREATED_DT, '%Y-%m-%d %H:%i:%s') AS CREATED_DT ,CREATED_BY ,DATE_FORMAT(UPDATED_DT, '%Y-%m-%d %H:%i:%s') AS UPDATED_DT ,UPDATED_BY ,USE_AT FROM TB_ISSUE
ORDER BY ISSUE_ID DESC
LIMIT #{limit} OFFSET #{offset}

mapper.insert:
/*
 SQL ID: www.api.isu.issue.Issue.insertIssue
 Description : 등록 Query
*/
INSERT INTO TB_ISSUE
(
    GRP_CD
    ,PRJ_ID
    ,ISSUE_NO
    ,ISSUE_KEY
    ,ISSUE_TYPE_CD
    ,SUMMARY
    ,DESCRIPTION
    ,PRIORITY_CD
    ,STATUS_CD
    ,REPORTER_ID
    ,ASSIGNEE_ID
    ,PARENT_ISSUE_ID
    ,START_DT
    ,DUE_DT
    ,ESTIMATE_MIN
    ,SPENT_MIN
    ,ORDER_IN_PRJ
    ,CREATED_DT
    ,CREATED_BY
    ,UPDATED_DT
    ,UPDATED_BY
    ,USE_AT
    ,USER_ID
)
VALUES
(
    #{grpCd}
    ,#{prjId}
    ,#{issueNo}
    ,#{issueKey}
    ,#{issueTypeCd}
    ,#{summary}
    ,#{description}
    ,#{priorityCd}
    ,#{statusCd}
    ,#{reporterId}
    ,#{assigneeId}
    ,#{parentIssueId}
    ,IFNULL(STR_TO_DATE(NULLIF(CONCAT(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{startDt}, 'T',' '), '.', 1),'Z',''),'T',' '), CASE WHEN LENGTH(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{startDt}, 'T',' '), '.', 1),'Z',''),'T',' '))=16 THEN ':00' ELSE '' END),''), '%Y-%m-%d %H:%i:%s'), NOW())
    ,IFNULL(STR_TO_DATE(NULLIF(CONCAT(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{dueDt}, 'T',' '), '.', 1),'Z',''),'T',' '), CASE WHEN LENGTH(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{dueDt}, 'T',' '), '.', 1),'Z',''),'T',' '))=16 THEN ':00' ELSE '' END),''), '%Y-%m-%d %H:%i:%s'), NOW())
    ,#{estimateMin}
    ,#{spentMin}
    ,#{orderInPrj}
    ,IFNULL(STR_TO_DATE(NULLIF(CONCAT(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{createdDt}, 'T',' '), '.', 1),'Z',''),'T',' '), CASE WHEN LENGTH(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{createdDt}, 'T',' '), '.', 1),'Z',''),'T',' '))=16 THEN ':00' ELSE '' END),''), '%Y-%m-%d %H:%i:%s'), NOW())
    ,#{createdBy}
    ,IFNULL(STR_TO_DATE(NULLIF(CONCAT(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{updatedDt}, 'T',' '), '.', 1),'Z',''),'T',' '), CASE WHEN LENGTH(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{updatedDt}, 'T',' '), '.', 1),'Z',''),'T',' '))=16 THEN ':00' ELSE '' END),''), '%Y-%m-%d %H:%i:%s'), NOW())
    ,#{updatedBy}
    ,#{useAt}
    ,#{userId}
)

mapper.delete:
/*
 SQL ID: www.api.isu.issue.Issue.deleteIssue
 Description : 삭제 Query
*/
DELETE FROM TB_ISSUE
WHERE ISSUE_ID = #{issueId}

mapper.select:
/* SQL ID: www.api.isu.issue.Issue.selectIssueBoardList Description : 칸반 보드용 전체 이슈 목록 */ SELECT ISSUE_ID ,GRP_CD ,PRJ_ID ,ISSUE_NO ,ISSUE_KEY ,ISSUE_TYPE_CD ,SUMMARY ,PRIORITY_CD ,STATUS_CD ,ASSIGNEE_ID ,DATE_FORMAT(DUE_DT, '%Y-%m-%d %H:%i:%s') AS DUE_DT ,ORDER_IN_PRJ FROM TB_ISSUE
USE_AT = 'Y'
AND GRP_CD = #{grpCd}
AND PRJ_ID = #{prjId}
ORDER BY STATUS_CD, ORDER_IN_PRJ ASC, PRIORITY_CD DESC, ISSUE_NO ASC

mapper.update:
/*
 SQL ID: www.api.isu.issue.Issue.updateIssueStatus
 Description : 칸반 보드에서 상태만 변경
*/
UPDATE TB_ISSUE
SET STATUS_CD  = #{statusCd},
    UPDATED_DT = NOW(),
    UPDATED_BY = #{updatedBy}
WHERE ISSUE_ID = #{issueId}
  AND USE_AT   = 'Y'
