mapper.select:
/* SQL ID: www.api.mmp.mapGrp.MapGrp.selectMapGrpList Description : 목록 Query */ SELECT MAP_GRP_ID ,GRP_NM ,GRP_CD ,DESC_TXT ,THUMB_FILE_GRP_ID ,USE_AT ,CREATED_BY ,DATE_FORMAT(CREATED_DT, '%Y-%m-%d %H:%i:%s') AS CREATED_DT ,UPDATED_BY ,DATE_FORMAT(UPDATED_DT, '%Y-%m-%d %H:%i:%s') AS UPDATED_DT
FROM TB_MAP_GRP
ORDER BY MAP_GRP_ID DESC
/* 페이징 하단 시작 */ LIMIT #{limit} OFFSET #{offset} /* 페이징 하단 끝 */

mapper.insert:
        /*
         SQL ID: www.api.mmp.mapGrp.MapGrp.insertMapGrp
         Description : 등록 Query 
        */
        INSERT INTO TB_MAP_GRP
        (
            GRP_NM
            ,GRP_CD
			,DESC_TXT
			,THUMB_FILE_GRP_ID
			,USE_AT
			,CREATED_BY
			,CREATED_DT
			,UPDATED_BY
			,UPDATED_DT

        )
        VALUES
        (
            #{grpNm}
            ,#{grpCd}
			,#{descTxt}
			,#{thumbFileGrpId}
			,#{useAt}
			,#{createdBy}
			,IFNULL(STR_TO_DATE(NULLIF(CONCAT(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{createdDt}, 'T',' '), '.', 1),'Z',''),'T',' '), CASE WHEN LENGTH(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{createdDt}, 'T',' '), '.', 1),'Z',''),'T',' '))=16 THEN ':00' ELSE '' END),''), '%Y-%m-%d %H:%i:%s'), NOW())
			,#{updatedBy}
			,IFNULL(STR_TO_DATE(NULLIF(CONCAT(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{updatedDt}, 'T',' '), '.', 1),'Z',''),'T',' '), CASE WHEN LENGTH(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{updatedDt}, 'T',' '), '.', 1),'Z',''),'T',' '))=16 THEN ':00' ELSE '' END),''), '%Y-%m-%d %H:%i:%s'), NOW())
        )

mapper.delete:
/*
 SQL ID: www.api.mmp.mapGrp.MapGrp.deleteMapGrp
 Description : 삭제 Query 
*/
DELETE FROM TB_MAP_GRP
WHERE MAP_GRP_ID = #{mapGrpId}
