mapper.select:
SELECT M.MAP_ID, M.TITLE, M.MAP_GRP_ID, MG.GRP_CD, MG.GRP_NM, M.MAP_NM, M.CONTENT, M.LAT, M.LNG, M.CREATE_USER, M.VIDEO_URL, M.LIKE_CNT, M.CMT_CNT, M.VIEW_CNT, DATE_FORMAT(M.CREATE_DATE, '%Y-%m-%d %H:%i:%s') AS CREATE_DATE, M.UPDATE_USER, DATE_FORMAT(M.UPDATE_DATE, '%Y-%m-%d %H:%i:%s') AS UPDATE_DATE FROM TB_MAP M LEFT JOIN TB_MAP_GRP MG ON MG.MAP_GRP_ID = M.MAP_GRP_ID
ORDER BY M.MAP_ID DESC
LIMIT #{limit} OFFSET #{offset}
