mapper.select:
/* SQL ID: www.api.sys.menuGroup.MenuGroup.selectMenuGroupList Description : 목록 Query */ SELECT MENU_GROUP_ID ,MENU_GROUP_NM ,RM ,USE_AT ,DATE_FORMAT(CREATED_DT, '%Y-%m-%d %H:%i:%s') AS CREATED_DT ,DATE_FORMAT(UPDATED_DT, '%Y-%m-%d %H:%i:%s') AS UPDATED_DT
FROM TB_MENU_GROUP
ORDER BY MENU_GROUP_ID DESC
/* 페이징 하단 시작 */ LIMIT #{limit} OFFSET #{offset} /* 페이징 하단 끝 */

mapper.insert:
        /*
         SQL ID: www.api.sys.menuGroup.MenuGroup.insertMenuGroup
         Description : 등록 Query 
        */
        INSERT INTO TB_MENU_GROUP
        (
            MENU_GROUP_NM
			,RM
			,USE_AT
			,CREATED_DT
			,UPDATED_DT

        )
        VALUES
        (
            #{menuGroupNm}
			,#{rm}
			,#{useAt}
			,IFNULL(STR_TO_DATE(NULLIF(CONCAT(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{createdDt}, 'T',' '), '.', 1),'Z',''),'T',' '), CASE WHEN LENGTH(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{createdDt}, 'T',' '), '.', 1),'Z',''),'T',' '))=16 THEN ':00' ELSE '' END),''), '%Y-%m-%d %H:%i:%s'), NOW())
			,IFNULL(STR_TO_DATE(NULLIF(CONCAT(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{updatedDt}, 'T',' '), '.', 1),'Z',''),'T',' '), CASE WHEN LENGTH(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{updatedDt}, 'T',' '), '.', 1),'Z',''),'T',' '))=16 THEN ':00' ELSE '' END),''), '%Y-%m-%d %H:%i:%s'), NOW())

        )

mapper.delete:
/*
 SQL ID: www.api.sys.menuGroup.MenuGroup.deleteMenuGroup
 Description : 삭제 Query 
*/
DELETE FROM TB_MENU_GROUP
WHERE MENU_GROUP_ID = #{menuGroupId}
