mapper.select:
/* SQL ID: www.api.pay.payment.Payment.selectPaymentList Description : 결제 목록 Query */ SELECT PAYMENT_ID ,ORDER_ID ,PG_CD ,PG_MID ,PG_TID ,PAY_METHOD_CD ,PAY_TOTAL_AMT ,PAY_APPROVED_AMT ,PAY_STATUS_CD ,DATE_FORMAT(REQ_DT, '%Y-%m-%d %H:%i:%s') AS REQ_DT ,DATE_FORMAT(RES_DT, '%Y-%m-%d %H:%i:%s') AS RES_DT ,RAW_REQUEST_JSON ,RAW_RESPONSE_JSON ,USE_AT ,DATE_FORMAT(CREATED_DT, '%Y-%m-%d %H:%i:%s') AS CREATED_DT ,CREATED_BY ,DATE_FORMAT(UPDATED_DT, '%Y-%m-%d %H:%i:%s') AS UPDATED_DT ,UPDATED_BY FROM TB_PAYMENT
ORDER BY PAYMENT_ID DESC
/* 페이징 하단 시작 */ LIMIT #{limit} OFFSET #{offset} /* 페이징 하단 끝 */

/* SQL ID: www.api.pay.payment.Payment.selectPaymentDetail Description : 결제 상세조회 Query */ SELECT PAYMENT_ID ,ORDER_ID ,PG_CD ,PG_MID ,PG_TID ,PAY_METHOD_CD ,PAY_TOTAL_AMT ,PAY_APPROVED_AMT ,PAY_STATUS_CD ,DATE_FORMAT(REQ_DT, '%Y-%m-%d %H:%i:%s') AS REQ_DT ,DATE_FORMAT(RES_DT, '%Y-%m-%d %H:%i:%s') AS RES_DT ,RAW_REQUEST_JSON ,RAW_RESPONSE_JSON ,USE_AT ,DATE_FORMAT(CREATED_DT, '%Y-%m-%d %H:%i:%s') AS CREATED_DT ,CREATED_BY ,DATE_FORMAT(UPDATED_DT, '%Y-%m-%d %H:%i:%s') AS UPDATED_DT ,UPDATED_BY FROM TB_PAYMENT WHERE PAYMENT_ID = #{paymentId}
AND USE_AT = #{useAt}
AND USE_AT = 'Y'

mapper.insert:
/*
 SQL ID: www.api.pay.payment.Payment.insertPayment
 Description : 결제 등록 Query
*/
INSERT INTO TB_PAYMENT
(
    ORDER_ID
    ,PG_CD
    ,PG_MID
    ,PG_TID
    ,PAY_METHOD_CD
    ,PAY_TOTAL_AMT
    ,PAY_APPROVED_AMT
    ,PAY_STATUS_CD
    ,REQ_DT
    ,RES_DT
    ,RAW_REQUEST_JSON
    ,RAW_RESPONSE_JSON
    ,USE_AT
    ,CREATED_DT
    ,CREATED_BY
    ,UPDATED_DT
    ,UPDATED_BY
)
VALUES
(
    #{orderId}
    ,#{pgCd}
    ,#{pgMid}
    ,#{pgTid}
    ,#{payMethodCd}
    ,#{payTotalAmt}
    ,#{payApprovedAmt}
    ,#{payStatusCd}
    ,IFNULL(STR_TO_DATE(NULLIF(CONCAT(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{reqDt}, 'T',' '), '.', 1),'Z',''),'T',' '), CASE WHEN LENGTH(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{reqDt}, 'T',' '), '.', 1),'Z',''),'T',' '))=16 THEN ':00' ELSE '' END),''), '%Y-%m-%d %H:%i:%s'), NOW())
    ,IFNULL(STR_TO_DATE(NULLIF(CONCAT(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{resDt}, 'T',' '), '.', 1),'Z',''),'T',' '), CASE WHEN LENGTH(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{resDt}, 'T',' '), '.', 1),'Z',''),'T',' '))=16 THEN ':00' ELSE '' END),''), '%Y-%m-%d %H:%i:%s'), NOW())
    ,#{rawRequestJson}
    ,#{rawResponseJson}
    ,#{useAt}
    ,IFNULL(STR_TO_DATE(NULLIF(CONCAT(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{createdDt}, 'T',' '), '.', 1),'Z',''),'T',' '), CASE WHEN LENGTH(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{createdDt}, 'T',' '), '.', 1),'Z',''),'T',' '))=16 THEN ':00' ELSE '' END),''), '%Y-%m-%d %H:%i:%s'), NOW())
    ,#{createdBy}
    ,IFNULL(STR_TO_DATE(NULLIF(CONCAT(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{updatedDt}, 'T',' '), '.', 1),'Z',''),'T',' '), CASE WHEN LENGTH(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{updatedDt}, 'T',' '), '.', 1),'Z',''),'T',' '))=16 THEN ':00' ELSE '' END),''), '%Y-%m-%d %H:%i:%s'), NOW())
    ,#{updatedBy}
)

mapper.update:
/*
 SQL ID: www.api.pay.payment.Payment.deletePayment
 Description : 결제 삭제(soft delete) Query
*/
UPDATE TB_PAYMENT
SET
    USE_AT = 'N',
    UPDATED_DT = NOW()

        , UPDATED_BY = #{updatedBy}

WHERE PAYMENT_ID = #{paymentId}
