mapper.select:
/* SQL ID: www.api.tsk.task.Task.selectTaskList Description : 목록 Query (PK: TASK_ID, 호환 alias TASK_ID) */ SELECT TASK_ID, GRP_CD, TITLE, CONTENT, STATUS_CD, PRIORITY_CD, DATE_FORMAT(START_DT, '%Y-%m-%d %H:%i:%s') AS START_DT, DATE_FORMAT(DUE_DT, '%Y-%m-%d %H:%i:%s') AS DUE_DT, DATE_FORMAT(DONE_DT, '%Y-%m-%d %H:%i:%s') AS DONE_DT, SORT_ORDR, USE_AT, DATE_FORMAT(CREATED_DT,'%Y-%m-%d %H:%i:%s') AS CREATED_DT, DATE_FORMAT(UPDATED_DT,'%Y-%m-%d %H:%i:%s') AS UPDATED_DT FROM TB_TASK
ORDER BY TASK_ID DESC
LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE TB_TASK
SET USE_AT = 'N', UPDATED_DT = NOW()   
WHERE TASK_ID = #{taskId}

mapper.select:
/* SQL ID: www.api.tsk.task.Task.selectTaskListByDate Description : 선택한 날짜(YYYY-MM-DD)의 Task 목록 */ SELECT TASK_ID, GRP_CD, TITLE, CONTENT, STATUS_CD, PRIORITY_CD, DATE_FORMAT(START_DT, '%Y-%m-%d %H:%i:%s') AS START_DT, DATE_FORMAT(DUE_DT, '%Y-%m-%d %H:%i:%s') AS DUE_DT, DATE_FORMAT(DONE_DT, '%Y-%m-%d %H:%i:%s') AS DONE_DT, SORT_ORDR, USE_AT, DATE_FORMAT(CREATED_DT,'%Y-%m-%d %H:%i:%s') AS CREATED_DT, DATE_FORMAT(UPDATED_DT,'%Y-%m-%d %H:%i:%s') AS UPDATED_DT FROM TB_TASK WHERE USE_AT = 'Y'
AND GRP_CD = #{grpCd}
AND ( (#{dueDate} IS NULL AND DATE(DUE_DT) = CURRENT_DATE()) OR (#{dueDate} IS NOT NULL AND DATE(DUE_DT) = #{dueDate}) ) ORDER BY STATUS_CD, DUE_DT, SORT_ORDR, TASK_ID DESC
