mapper.select:
/* SQL ID: www.api.tst.testt.Testt.selectTesttList Description : 목록 Query */ SELECT TESTT_IDX ,GRP_CD ,BOARD_CD ,CATEGORY_CD ,STATUS_CD ,NOTICE_AT ,PIN_AT ,SECRET_AT ,TITLE ,SUB_TITLE ,SUMMARY_TXT ,CONTENT_HTML ,TAGS ,THUMB_URL ,ATTACH_JSON ,DATE_FORMAT(PUBLISH_START_DT, '%Y-%m-%d %H:%i:%s') AS PUBLISH_START_DT ,DATE_FORMAT(PUBLISH_END_DT, '%Y-%m-%d %H:%i:%s') AS PUBLISH_END_DT ,SORT_ORDR ,VIEW_CNT ,LIKE_CNT ,COMMENT_CNT ,PLACE_NM ,PLACE_ADDR ,LAT ,LNG ,USE_AT ,DEL_AT ,DATE_FORMAT(DEL_DT, '%Y-%m-%d %H:%i:%s') AS DEL_DT ,DEL_BY ,DATE_FORMAT(CREATED_DT, '%Y-%m-%d %H:%i:%s') AS CREATED_DT ,CREATED_BY ,DATE_FORMAT(UPDATED_DT, '%Y-%m-%d %H:%i:%s') AS UPDATED_DT ,UPDATED_BY
FROM TB_TESTT
ORDER BY TESTT_IDX DESC
/* 페이징 하단 시작 */ LIMIT #{limit} OFFSET #{offset} /* 페이징 하단 끝 */

mapper.insert:
        /*
         SQL ID: www.api.tst.testt.Testt.insertTestt
         Description : 등록 Query 
        */
        INSERT INTO TB_TESTT
        (
            GRP_CD
			,BOARD_CD
			,CATEGORY_CD
			,STATUS_CD
			,NOTICE_AT
			,PIN_AT
			,SECRET_AT
			,TITLE
