			,DEL_AT
			,DEL_DT
			,DEL_BY
			,CREATED_DT
			,CREATED_BY
			,UPDATED_DT
			,UPDATED_BY

        )
        VALUES
        (
            #{grpCd}
			,#{boardCd}
			,#{categoryCd}
			,#{statusCd}
			,#{noticeAt}
			,#{pinAt}
			,#{secretAt}
