mapper.select:
/* SQL ID: www.api.usr.user.User.selectUserList Description : 목록 Query */ SELECT USER_ID ,EMAIL ,PASSWORD_HASH ,USER_NM ,TELNO ,USER_STTS_CD ,EMAIL_VRFCT_AT ,PW_FAILR_CNT ,DATE_FORMAT(LAST_LOGIN_DT, '%Y-%m-%d %H:%i:%s') AS LAST_LOGIN_DT ,USE_AT ,DATE_FORMAT(CREATED_DT, '%Y-%m-%d %H:%i:%s') AS CREATED_DT ,CREATED_BY ,DATE_FORMAT(UPDATED_DT, '%Y-%m-%d %H:%i:%s') AS UPDATED_DT ,UPDATED_BY ,AUTH_TYPE
FROM TB_USER
ORDER BY USER_ID DESC
/* 페이징 하단 시작 */ LIMIT #{limit} OFFSET #{offset} /* 페이징 하단 끝 */

mapper.insert:
        /*
         SQL ID: www.api.usr.user.User.insertUser
         Description : 등록 Query 
        */
        INSERT INTO TB_USER
        (
            EMAIL
			,PASSWORD_HASH
			,USER_NM
			,TELNO
			,USER_STTS_CD
			,EMAIL_VRFCT_AT
			,PW_FAILR_CNT
			,LAST_LOGIN_DT
			,USE_AT
			,CREATED_DT
			,CREATED_BY
			,UPDATED_DT
			,UPDATED_BY
			,AUTH_TYPE

        )
        VALUES
        (
            #{email}
			,#{passwordHash}
			,#{userNm}
			,#{telno}
			,#{userSttsCd}
			,#{emailVrfctAt}
			,#{pwFailrCnt}
			,IFNULL(STR_TO_DATE(NULLIF(CONCAT(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{lastLoginDt}, 'T',' '), '.', 1),'Z',''),'T',' '), CASE WHEN LENGTH(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{lastLoginDt}, 'T',' '), '.', 1),'Z',''),'T',' '))=16 THEN ':00' ELSE '' END),''), '%Y-%m-%d %H:%i:%s'), NOW())
			,#{useAt}
			,IFNULL(STR_TO_DATE(NULLIF(CONCAT(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{createdDt}, 'T',' '), '.', 1),'Z',''),'T',' '), CASE WHEN LENGTH(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{createdDt}, 'T',' '), '.', 1),'Z',''),'T',' '))=16 THEN ':00' ELSE '' END),''), '%Y-%m-%d %H:%i:%s'), NOW())
			,#{createdBy}
			,IFNULL(STR_TO_DATE(NULLIF(CONCAT(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{updatedDt}, 'T',' '), '.', 1),'Z',''),'T',' '), CASE WHEN LENGTH(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{updatedDt}, 'T',' '), '.', 1),'Z',''),'T',' '))=16 THEN ':00' ELSE '' END),''), '%Y-%m-%d %H:%i:%s'), NOW())
			,#{updatedBy}
			,#{authType}

        )

mapper.delete:
/*
 SQL ID: www.api.usr.user.User.deleteUser
 Description : 삭제 Query 
*/
DELETE FROM TB_USER
WHERE USER_ID = #{userId}
