mapper.select:
/* SQL ID: www.api.isu.issueComment.issueComment.selectissueCommentList Description : 목록 Query */ SELECT COMMENT_ID ,GRP_CD ,ISSUE_ID ,CONTENT ,WRITER_ID ,DATE_FORMAT(CREATED_DT, '%Y-%m-%d %H:%i:%s') AS CREATED_DT ,CREATED_BY ,DATE_FORMAT(UPDATED_DT, '%Y-%m-%d %H:%i:%s') AS UPDATED_DT ,UPDATED_BY ,USE_AT
FROM TB_ISSUE_COMMENT
AND ISSUE_ID = #{issueId}
ORDER BY COMMENT_ID DESC
/* 페이징 하단 시작 */ LIMIT #{limit} OFFSET #{offset} /* 페이징 하단 끝 */

mapper.insert:
        /*
         SQL ID: www.api.isu.issueComment.issueComment.insertissueComment
         Description : 등록 Query 
        */
        INSERT INTO TB_ISSUE_COMMENT
        (
            GRP_CD
			,ISSUE_ID
			,CONTENT
			,WRITER_ID
			,CREATED_DT
			,CREATED_BY
			,UPDATED_DT
			,UPDATED_BY
			,USE_AT

        )
        VALUES
        (
            #{grpCd}
			,#{issueId}
			,#{content}
			,#{writerId}
			,IFNULL(STR_TO_DATE(NULLIF(CONCAT(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{createdDt}, 'T',' '), '.', 1),'Z',''),'T',' '), CASE WHEN LENGTH(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{createdDt}, 'T',' '), '.', 1),'Z',''),'T',' '))=16 THEN ':00' ELSE '' END),''), '%Y-%m-%d %H:%i:%s'), NOW())
			,#{createdBy}
			,IFNULL(STR_TO_DATE(NULLIF(CONCAT(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{updatedDt}, 'T',' '), '.', 1),'Z',''),'T',' '), CASE WHEN LENGTH(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{updatedDt}, 'T',' '), '.', 1),'Z',''),'T',' '))=16 THEN ':00' ELSE '' END),''), '%Y-%m-%d %H:%i:%s'), NOW())
			,#{updatedBy}
			,#{useAt}

        )

mapper.delete:
/*
 SQL ID: www.api.isu.issueComment.issueComment.deleteissueComment
 Description : 삭제 Query 
*/
DELETE FROM TB_ISSUE_COMMENT
WHERE COMMENT_ID = #{commentId}
