mapper.select:
/* SQL ID: www.api.isu.prj.prj.selectprjList Description : 목록 Query */ SELECT PRJ_ID ,GRP_CD ,PRJ_KEY ,PRJ_NM ,PRJ_DESC ,STATUS_CD ,COLOR_CD ,DATE_FORMAT(CREATED_DT, '%Y-%m-%d %H:%i:%s') AS CREATED_DT ,CREATED_BY ,DATE_FORMAT(UPDATED_DT, '%Y-%m-%d %H:%i:%s') AS UPDATED_DT ,UPDATED_BY ,USE_AT
FROM TB_PRJ
ORDER BY PRJ_ID DESC
/* 페이징 하단 시작 */ LIMIT #{limit} OFFSET #{offset} /* 페이징 하단 끝 */

mapper.insert:
        /*
         SQL ID: www.api.isu.prj.prj.insertprj
         Description : 등록 Query 
        */
        INSERT INTO TB_PRJ
        (
            GRP_CD
			,PRJ_KEY
			,PRJ_NM
			,PRJ_DESC
			,STATUS_CD
			,COLOR_CD
			,CREATED_DT
			,CREATED_BY
			,UPDATED_DT
			,UPDATED_BY
			,USE_AT
			,USER_ID

        )
        VALUES
        (
            #{grpCd}
			,#{prjKey}
			,#{prjNm}
			,#{prjDesc}
			,#{statusCd}
			,#{colorCd}
			,IFNULL(STR_TO_DATE(NULLIF(CONCAT(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{createdDt}, 'T',' '), '.', 1),'Z',''),'T',' '), CASE WHEN LENGTH(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{createdDt}, 'T',' '), '.', 1),'Z',''),'T',' '))=16 THEN ':00' ELSE '' END),''), '%Y-%m-%d %H:%i:%s'), NOW())
			,#{createdBy}
			,IFNULL(STR_TO_DATE(NULLIF(CONCAT(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{updatedDt}, 'T',' '), '.', 1),'Z',''),'T',' '), CASE WHEN LENGTH(REPLACE(REPLACE(SUBSTRING_INDEX(REPLACE(#{updatedDt}, 'T',' '), '.', 1),'Z',''),'T',' '))=16 THEN ':00' ELSE '' END),''), '%Y-%m-%d %H:%i:%s'), NOW())
			,#{updatedBy}
			,#{useAt}
			,#{userId}

        )

mapper.delete:
/*
 SQL ID: www.api.isu.prj.prj.deleteprj
 Description : 삭제 Query 
*/
DELETE FROM TB_PRJ
WHERE PRJ_ID = #{prjId}
